import { Component } from '@angular/core';
import {Task} from "../dto/task";
import {HttpClient} from "@angular/common/http";
@Component({
  selector: 'app-task-container',
  templateUrl: './task-container.component.html',
  styleUrls: ['./task-container.component.scss']
})
export class TaskContainerComponent {


  taskList: Array<Task> = [];

  constructor(private http: HttpClient) {
    http.get<Array<Task>>("http://localhost:8080/app/api/v1/task")
      .subscribe(taskList=>{ this.taskList=taskList;
        console.log(taskList)
      });

  }

  saveTask(txt: HTMLInputElement) {
    if (!txt.value.trim()){
      txt.select();
      return;
    }
    this.http.post<Task>("http://localhost:8080/app/api/v1/task",new Task(0,txt.value,'NOT_COMPLETED')).subscribe(task=>{
      this.taskList.push(task);
      txt.value='';
      txt.focus();
    });
  }

  delete(task: Task) {
    this.http.delete(`http://localhost:8080/app/api/v1/task/${task.id}`).subscribe(data=>{
      const index:number = this.taskList.indexOf(task);
      this.taskList.splice(index,1);
    })
  }

}
<div class="px-3 d-flex gap-2">
  <input #txt class="form-control text-center" type="text" placeholder="Enter a new task">
  <button class="btn btn-primary" (click)="saveTask(txt)">Add</button>
</div>

<ng-container *ngFor="let task of taskList">
  <div class="px-3 py-2">
    <div class="form-check d-flex justify-content-between">
      <div>
        <input [checked]="task.status === 'COMPLETED'"
               id="task-{{task.id}}" class="form-check-input" type="checkbox">
        <label id="task-{{task.id}}" class="form-check-label">{{task.description}}</label>
      </div>
      <span class="trash" title="" (click)="delete(task)">
      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-trash3" viewBox="0 0 16 16">
      <path d="M6.5 1h3a.5.5 0 0 1 .5.5v1H6v-1a.5.5 0 0 1 .5-.5ZM11 2.5v-1A1.5 1.5 0 0 0 9.5 0h-3A1.5 1.5 0 0 0 5 1.5v1H2.506a.58.58 0 0 0-.01 0H1.5a.5.5 0 0 0 0 1h.538l.853 10.66A2 2 0 0 0 4.885 16h6.23a2 2 0 0 0 1.994-1.84l.853-10.66h.538a.5.5 0 0 0 0-1h-.995a.59.59 0 0 0-.01 0H11Zm1.958 1-.846 10.58a1 1 0 0 1-.997.92h-6.23a1 1 0 0 1-.997-.92L3.042 3.5h9.916Zm-7.487 1a.5.5 0 0 1 .528.47l.5 8.5a.5.5 0 0 1-.998.06L5 5.03a.5.5 0 0 1 .47-.53Zm5.058 0a.5.5 0 0 1 .47.53l-.5 8.5a.5.5 0 1 1-.998-.06l.5-8.5a.5.5 0 0 1 .528-.47ZM8 4.5a.5.5 0 0 1 .5.5v8.5a.5.5 0 0 1-1 0V5a.5.5 0 0 1 .5-.5Z"/>
      </svg>
    </span>
    </div>
  </div>
</ng-container>
